import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitted(true);
  };

  return (
    <div>
      <div className="heading-container">
        <h1 className="text-center text-shadow">FEEDBACK FORM</h1>
      </div>
      <div className="form-container">
        <div className='card p-4' style={{ width: '70%', height: '65%' }}>
          {isSubmitted ? (
            <div className="alert alert-success" role="alert">
              Your form has been successfully submitted!
            </div>
          ) : (
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="exampleInputPassword1" className="form-label">Name<span className="text-danger">*</span></label>
                <input type="text" className="form-control mb-3" id="exampleInputPassword1" required />
                <div className="form-text">Mandatory.</div>
              </div>
              <div className="mb-3">
                <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
              </div>
              <div className="mb-3 row">
                <label htmlFor="exampleText1" className="col-form-label">Text<span className="text-danger">*</span></label>
                <div className="col-sm-7">
                  <input type="text" className="form-control" id="exampleText1" style={{ height: '100px' }} required />
                  <div className="form-text">Mandatory.</div>
                </div>
              </div>
              <button type="submit" className="btn btn-success">Submit</button>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
